document.addEventListener("turbolinks:load",()=>{const t=['<%= asset_path "scrolling_bg1.png" %>','<%= asset_path "scrolling_bg2.png" %>','<%= asset_path "scrolling_bg3.png" %>','<%= asset_path "scrolling_bg4.png" %>','<%= asset_path "scrolling_bg5.png" %>','<%= asset_path "scrolling_bg6.png" %>'],n=document.querySelector(".background-container");let s=0;setInterval(()=>{s=(s+1)%t.length,n.style.backgroundImage=`url(${t[s]})`},6e3)});