document.addEventListener("turbolinks:load",()=>{console.log(">>> turbolinks:load event fired in game_ui.js <<<");const e=document.querySelectorAll(".grid-cell");if(console.log("Number of grid cells:",e.length),0===e.length)return void console.warn("No .grid-cell elements found!");e.forEach(e=>{e.addEventListener("mouseover",()=>{e.classList.contains("occupied")||(e.style.backgroundColor="#ccc")}),e.addEventListener("mouseout",()=>{e.classList.contains("occupied")||(e.style.backgroundColor="")}),e.addEventListener("click",()=>{const l=e.dataset.x,t=e.dataset.y;console.log(`Cell clicked at (${l}, ${t})`)})});const l=document.getElementById("pauseMenu"),t=document.getElementById("pauseMenuToggle"),n=document.getElementById("pauseMenuClose");l&&t&&n&&(t.addEventListener("click",()=>{l.style.display="block",t.style.display="none"}),n.addEventListener("click",()=>{l.style.display="none",t.style.display="block"}),document.addEventListener("click",e=>{l.contains(e.target)||t.contains(e.target)||"block"!==l.style.display||(l.style.display="none",t.style.display="block",console.log("Pause menu closed by clicking outside."))}))});